import React, { useState, use } from 'react';
import { TextField, Button, Box } from '@mui/material';
import Grid from '@mui/material/Grid';
import axios from 'axios';

const Form = ({ fetchContacts, currentContact, setCurrentContact }) => {
  const [contact, setContact] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    company: '',
    jobTitle: '',
  });

  useEffect(() => {
    if (currentContact) {
      setContact(currentContact);
    }
  }, [currentContact]);

  const handleChange = (e) => {
    setContact({
      ...contact,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Frontend validation
    if (!contact.firstName || !contact.lastName || !contact.email || !contact.phone || !contact.company || !contact.jobTitle) {
      alert('All fields are required.');
      return;
    }

    if (!contact.email.endsWith('@gmail.com')) {
      alert('Email must be a valid Gmail address (e.g., example@gmail.com).');
      return;
    }

    if (contact.phone.length !== 10 || !/^\d+$/.test(contact.phone)) {
      alert('Phone number must be exactly 10 digits.');
      return;
    }

    try {
      const response = await axios.post('http://localhost:3001/contacts', contact);
      alert(response.data.message || 'Contact added successfully!');
      fetchContacts();
      setContact({
        firstName: '',
        lastName: '',
        email: '',
        phone: '',
        company: '',
        jobTitle: '',
      });
    } catch (error) {
      alert(error.response?.data?.message || 'An error occurred while adding the contact.');
      console.error('Error adding contact:', error);
    }
  };

  return (
    <Box component="form" onSubmit={handleSubmit} sx={{ p: 2 }}>
      <Grid container spacing={2}>
        {['firstName', 'lastName', 'email', 'phone', 'company', 'jobTitle'].map((field) => (
          <Grid item xs={12} sm={6} key={field}>
            <TextField
              fullWidth
              label={field.replace(/^\w/, (c) => c.toUpperCase())}
              name={field}
              value={contact[field]}
              onChange={handleChange}
              required
            />
          </Grid>
        ))}
        <Grid item xs={12} sm={6}>
          <Button type="submit" variant="contained" fullWidth>
            Add Contact
          </Button>
        </Grid>
      </Grid>
    </Box>
  );
};

export default Form;
