import React, {useState, useEffect} from 'react';
import { Container, Typography } from '@mui/material';
import axios from 'axios';
import Form from './components/Form';
import Tables from './components/Tables';

const App = () => {
  const [contacts, setContacts] = useState([]);

  const fetchContacts = async () => {
    try {
      const response = await axios.get('http://localhost:3001/contacts');
      setContacts(response.data);
    } catch (error) {
      console.error('Error fetching contacts:', error);
    }
  };

  const deleteContact = async (id) => {
    try {
      await axios.delete(`http://localhost:3001/contacts/${id}`);
      fetchContacts();
    } catch (error) {
      console.error('Error deleting contact:', error);
    }
  };

  const editContact = async (updatedContact) => {
    try {
      await axios.put(`http://localhost:3001/contacts/${updatedContact._id}`, updatedContact);
      fetchContacts();
    } catch (error) {
      console.error('Error editing contact:', error);
    }
  };


  useEffect(() => {
    useEffect(() => {
      fetchContacts();
    }, []);    
}, []);


  return (
    <Container>
      <Typography variant="h4" align="center" sx={{ my: 4 }}>
        Contact List
      </Typography>
      <Form fetchContacts={fetchContacts} />
      <Tables contacts={contacts} deleteContact={deleteContact} editContact={editContact} />
    </Container>
  )
}

export default App;
